package week1.day2;

public class LearnFactorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//n=5
		int fact=1;
		for(int i=1;i<=5;i++) {
			//1*2*3*4*5
			fact=fact*i;//1*1==
			//fact=1
			
			//i=2.fact=1*2=2
			//i=3,fact=2*3=6
			//i=4,fact=6*4=24
		//i=5,fact= 24*5=120
			System.out.println(fact+"value for"+i);

		
		}
		
		System.out.println(fact);
		
		
	}

}
