package week1.day2;

public class LearnSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int month=10;
		
		switch(month) {
		
		case 1:
			System.out.println("January");
			break;
		case 2:
			System.out.println("Feb");
			break;
		case 7:
			System.out.println("July");
			break;			
		default :
			System.out.println("No months available");
		  		
		
		}
		
		
		
		
		
		
		
		

	}

}
